"use client";

import { useState } from "react";
import { Slider } from "@/registry/default/ui/slider";

export default function SliderDemo() {
  const [value, setValue] = useState([50]);
  const bufferValue = 75;

  return (
    <div className="w-full space-y-8 px-4">
      <div className="space-y-2">
        <h3 className="font-medium text-sm">Basic Slider</h3>
        <Slider className="w-full" onValueChange={setValue} value={value} />
        <p className="text-muted-foreground text-xs">Value: {value[0]}</p>
      </div>

      <div className="space-y-2">
        <h3 className="font-medium text-sm">Slider with Buffer</h3>
        <Slider
          bufferValue={bufferValue}
          className="w-full"
          onValueChange={setValue}
          value={value}
        />
        <p className="text-muted-foreground text-xs">
          Value: {value[0]} | Buffer: {bufferValue}%
        </p>
      </div>

      <div className="space-y-2">
        <h3 className="font-medium text-sm">Range Slider</h3>
        <Slider
          className="w-full"
          defaultValue={[20, 80]}
          max={100}
          min={0}
          step={1}
        />
        <p className="text-muted-foreground text-xs">Range: 20 - 80</p>
      </div>

      <div className="space-y-2">
        <h3 className="font-medium text-sm">Disabled Slider</h3>
        <Slider className="w-full" defaultValue={[30]} disabled />
        <p className="text-muted-foreground text-xs">Disabled state</p>
      </div>
    </div>
  );
}
