"use client";

import { cva, type VariantProps } from "class-variance-authority";
import {
  ListMusicIcon,
  MusicIcon,
  PauseIcon,
  PlayIcon,
  RadioIcon,
  XIcon,
} from "lucide-react";
import type React from "react";
import {
  formatDuration,
  isLive,
  type Track,
} from "@/registry/default/lib/audio";
import { useAudioStore } from "@/registry/default/lib/audio-store";
import { cn } from "@/registry/default/lib/utils";
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/registry/default/ui/avatar";
import { Badge } from "@/registry/default/ui/badge";
import { Button } from "@/registry/default/ui/button";
import {
  Empty,
  EmptyDescription,
  EmptyHeader,
  EmptyMedia,
  EmptyTitle,
} from "@/registry/default/ui/empty";
import {
  Item,
  ItemActions,
  ItemContent,
  ItemDescription,
  ItemMedia,
  ItemTitle,
} from "@/registry/default/ui/item";
import { ScrollArea } from "@/registry/default/ui/scroll-area";
import {
  SortableDragHandle,
  SortableItem,
  SortableList,
} from "@/registry/default/ui/sortable-list";

/**
 * Props for the AudioTrack component.
 * Supports two modes: store mode (using trackId) or controlled mode (using track).
 */
export type AudioTrackProps = {
  trackId?: string | number;
  track?: Track;
  index?: number;
  onClick?: () => void;
  onRemove?: (trackId: string) => void;
  showRemove?: boolean;
  showPlayPause?: boolean;
  showDragHandle?: boolean;
  showCover?: boolean;
  className?: string;
} & (
  | { trackId: string | number; track?: never }
  | { track: Track; trackId?: never }
  | { trackId?: never; track?: never }
);

function getPlayPauseTitle(isCurrent: boolean, isPlaying: boolean): string {
  if (!isCurrent) {
    return "Play this track";
  }
  if (isPlaying) {
    return "Pause";
  }
  return "Play";
}

function renderTrackMedia(
  showDragHandle: boolean,
  showCover: boolean,
  track: Track,
  index?: number
) {
  if (showDragHandle && showCover) {
    const coverImage = track.artwork || track.images?.[0];
    return (
      <div className="flex items-center gap-2">
        <SortableDragHandle />
        {coverImage ? (
          <Avatar className="rounded-sm">
            <AvatarImage
              alt={track.title}
              className="object-cover"
              src={coverImage}
            />
            <AvatarFallback className="rounded-sm">
              <MusicIcon className="size-4 text-muted-foreground" />
            </AvatarFallback>
          </Avatar>
        ) : (
          <div className="flex size-10 items-center justify-center rounded-sm bg-muted">
            <MusicIcon className="size-4 text-muted-foreground" />
          </div>
        )}
      </div>
    );
  }

  if (showDragHandle) {
    return <SortableDragHandle />;
  }

  if (showCover) {
    const coverImage = track.artwork || track.images?.[0];
    if (coverImage) {
      return (
        <Avatar className="rounded-sm">
          <AvatarImage
            alt={track.title}
            className="object-cover"
            src={coverImage}
          />
          <AvatarFallback className="rounded-sm">
            <MusicIcon className="size-4 text-muted-foreground" />
          </AvatarFallback>
        </Avatar>
      );
    }
    return (
      <div className="flex size-10 items-center justify-center rounded-sm bg-muted">
        <MusicIcon className="size-4 text-muted-foreground" />
      </div>
    );
  }

  const displayIndex = index !== undefined ? index + 1 : "";
  return (
    <span className="text-muted-foreground/60 text-xs">{displayIndex}</span>
  );
}

function AudioTrack({
  trackId,
  track: externalTrack,
  index,
  onClick,
  onRemove,
  showRemove = false,
  showPlayPause = true,
  showDragHandle = false,
  showCover = true,
  className,
}: AudioTrackProps) {
  const queue = useAudioStore((state) => state.queue);
  const currentTrack = useAudioStore((state) => state.currentTrack);
  const isPlaying = useAudioStore((state) => state.isPlaying);
  const duration = useAudioStore((state) => state.duration);
  const togglePlay = useAudioStore((state) => state.togglePlay);
  const setQueueAndPlay = useAudioStore((state) => state.setQueueAndPlay);

  const track =
    externalTrack ??
    (trackId ? queue.find((t) => String(t.id) === String(trackId)) : undefined);

  if (!track) {
    return null;
  }

  const isCurrent = currentTrack?.id === track.id;
  const actualIsPlaying = isPlaying && isCurrent;
  const isLiveTrack = isLive(track);

  const trackDuration = isCurrent && duration > 0 ? duration : track.duration;

  const handleRemove = (e: React.MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();
    if (track.id && onRemove) {
      onRemove(String(track.id));
    }
  };

  const handlePlayPause = (e: React.MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();

    if (isCurrent) {
      togglePlay();
    } else {
      const trackIndex = queue.findIndex((t) => t.id === track.id);
      if (trackIndex >= 0) {
        setQueueAndPlay(queue, trackIndex);
      }
    }
  };

  return (
    <Item
      className={cn(
        "w-full cursor-pointer transition-all hover:bg-secondary/50",
        isCurrent && "bg-secondary/80 backdrop-blur-sm",
        className
      )}
      onClick={(e) => {
        e.stopPropagation();
        e.preventDefault();
        onClick?.();
      }}
      size="sm"
      variant={isCurrent ? "outline" : "default"}
    >
      <ItemMedia>
        {renderTrackMedia(showDragHandle, showCover, track, index)}
      </ItemMedia>
      <ItemContent className="min-w-0 flex-1 gap-0 overflow-hidden">
        <div className="flex items-center gap-1.5">
          <ItemTitle className="truncate font-medium text-sm">
            {track.title}
          </ItemTitle>
          {isLiveTrack && (
            <Badge className="bg-destructive/10 px-1 py-0.5 font-medium text-[10px] text-destructive uppercase leading-none">
              <RadioIcon className="size-2.5" />
              Live
            </Badge>
          )}
        </div>
        <ItemDescription className="text-xs">{track.artist}</ItemDescription>
      </ItemContent>
      {!isLiveTrack && trackDuration !== undefined && (
        <ItemContent className="flex-none text-center">
          <ItemDescription>{formatDuration(trackDuration)}</ItemDescription>
        </ItemContent>
      )}
      <ItemActions>
        {showRemove && !isCurrent && onRemove && (
          <Button
            onClick={handleRemove}
            size="icon-sm"
            title="Remove"
            variant="ghost"
          >
            <XIcon />
          </Button>
        )}
        {showPlayPause && (
          <Button
            onClick={handlePlayPause}
            size="icon-sm"
            title={getPlayPauseTitle(isCurrent, actualIsPlaying)}
            variant="ghost"
          >
            {actualIsPlaying ? (
              <PauseIcon className="size-4 fill-current" />
            ) : (
              <PlayIcon className="size-4 fill-current" />
            )}
          </Button>
        )}
      </ItemActions>
    </Item>
  );
}

AudioTrack.displayName = "AudioTrack";

const audioTrackListVariants = cva("w-full", {
  variants: {
    variant: {
      default: "space-y-2",
      grid: "grid grid-cols-1 gap-2 xl:grid-cols-2",
    },
  },
  defaultVariants: {
    variant: "default",
  },
});

/**
 * Props for the AudioTrackList component.
 * Can operate in store mode (reads from global queue) or controlled mode (uses provided tracks).
 */
export type AudioTrackListProps = {
  tracks?: Track[];
  onTrackSelect?: (index: number, track?: Track) => void;
  onTrackRemove?: (trackId: string) => void;
  sortable?: boolean;
  showCover?: boolean;
  emptyLabel?: string;
  emptyDescription?: string;
  filterQuery?: string;
  filterFn?: (track: Track) => boolean;
  className?: string;
} & VariantProps<typeof audioTrackListVariants>;

function AudioTrackList({
  tracks: externalTracks,
  onTrackSelect,
  onTrackRemove,
  sortable = false,
  showCover = true,
  variant = "default",
  emptyLabel = "No tracks found",
  emptyDescription = "Try adding some tracks",
  filterQuery,
  filterFn,
  className,
}: AudioTrackListProps) {
  const queue = useAudioStore((state) => state.queue);
  const currentTrack = useAudioStore((state) => state.currentTrack);
  const setQueueAndPlay = useAudioStore((state) => state.setQueueAndPlay);
  const togglePlay = useAudioStore((state) => state.togglePlay);
  const setQueue = useAudioStore((state) => state.setQueue);
  const currentQueueIndex = useAudioStore((state) => state.currentQueueIndex);

  let tracks = externalTracks ?? queue;

  if (filterFn) {
    tracks = tracks.filter(filterFn);
  } else if (filterQuery?.trim()) {
    const query = filterQuery.toLowerCase();
    tracks = tracks.filter(
      (track: Track) =>
        track.title?.toLowerCase().includes(query) ||
        track.artist?.toLowerCase().includes(query)
    );
  }

  const isFiltered = (filterQuery?.trim().length ?? 0) > 0 || !!filterFn;
  const isExternalTracks = !!externalTracks;

  const handleAutoReorder = (reorderedTracks: Track[]) => {
    if (!(isFiltered || isExternalTracks)) {
      const newIndex =
        currentTrack?.id !== undefined
          ? reorderedTracks.findIndex((t) => t.id === currentTrack.id)
          : -1;

      let finalIndex = 0;
      if (newIndex >= 0) {
        finalIndex = newIndex;
      } else if (
        currentQueueIndex >= 0 &&
        currentQueueIndex < reorderedTracks.length
      ) {
        finalIndex = currentQueueIndex;
      }

      setQueue(reorderedTracks, finalIndex);
    }
  };

  if (tracks.length === 0) {
    return (
      <Empty className={cn("mx-auto size-full border bg-muted/30", className)}>
        <EmptyHeader>
          <EmptyMedia variant="icon">
            <ListMusicIcon />
          </EmptyMedia>
          <EmptyTitle>{emptyLabel}</EmptyTitle>
          <EmptyDescription className="text-xs/relaxed">
            {emptyDescription}
          </EmptyDescription>
        </EmptyHeader>
      </Empty>
    );
  }

  const renderTrack = (track: Track, index: number, isOverlay = false) => {
    const handleTrackClick = () => {
      if (isExternalTracks) {
        onTrackSelect?.(index, track);
      } else {
        const queueIndex = queue.findIndex((t) => t.id === track.id);
        if (queueIndex >= 0) {
          if (currentTrack?.id === track.id) {
            togglePlay();
          } else {
            setQueueAndPlay(queue, queueIndex);
          }
          const originalTrack = queue[queueIndex];
          onTrackSelect?.(queueIndex, originalTrack);
        } else {
          onTrackSelect?.(index, track);
        }
      }
    };

    if (!track.id) {
      return null;
    }

    const showDragHandle = sortable && !isOverlay;

    return (
      <AudioTrack
        index={index}
        key={track.id}
        onClick={handleTrackClick}
        onRemove={onTrackRemove}
        showCover={showCover}
        showDragHandle={showDragHandle}
        showRemove={!!onTrackRemove}
        track={track}
      />
    );
  };

  const content = sortable ? (
    <SortableList
      className={
        variant === "grid" ? "grid grid-cols-1 gap-2 xl:grid-cols-2" : "gap-1"
      }
      items={tracks
        .filter((t) => t.id !== undefined)
        .map((t) => ({ id: String(t.id) }))}
      onChange={(reorderedTracks) => {
        const reorderedTrackIds = reorderedTracks.map((t) => t.id);
        const reorderedFullTracks = reorderedTrackIds
          .map((id) => tracks.find((t) => String(t.id) === id))
          .filter((t): t is Track => t !== undefined);
        handleAutoReorder(reorderedFullTracks);
      }}
      renderItem={(item, index, isOverlay = false) => {
        const track = tracks.find((t) => String(t.id) === item.id);
        if (!track?.id) {
          return null;
        }
        const originalIndex = tracks.findIndex((t) => t.id === track.id);
        const trackIndex = originalIndex >= 0 ? originalIndex : index;

        const trackContent = renderTrack(track, trackIndex, isOverlay);

        return (
          <SortableItem id={String(track.id)} key={track.id}>
            {trackContent}
          </SortableItem>
        );
      }}
    />
  ) : (
    <div className={cn(audioTrackListVariants({ variant }))}>
      {tracks.map((track, index) => renderTrack(track, index))}
    </div>
  );

  return (
    <ScrollArea
      className={cn("w-full scroll-pt-2 scroll-pb-1.5 pt-1", className)}
    >
      {content}
    </ScrollArea>
  );
}

export { AudioTrack, AudioTrackList };

export type Track = {
  id?: string | number;
  url: string;
  title?: string;
  artist?: string;
  artwork?: string;
  images?: string[];
  duration?: number;
  album?: string;
  genre?: string;
  live?: boolean;
  [key: string]: unknown;
};

type LoadParams = {
  url: string;
  startTime?: number;
  isLiveStream?: boolean;
};

type SetVolumeParams = {
  volume: number;
  fadeTime?: number;
};

type FadeVolumeParams = {
  audio: HTMLAudioElement;
  targetVolume: number;
  duration: number;
};

const LIVE_STREAM_PATTERNS = [
  /\/live\./i,
  /\/stream/i,
  /\/radio/i,
  /\.m3u8$/i,
  /\.pls$/i,
  /\.aac$/i,
  /icecast|shoutcast/i,
];

class AudioLib {
  private audio: HTMLAudioElement | null = null;
  private isInitialized = false;
  private playPromise: Promise<void> | null = null;
  private lastVolume = 1;
  private fadeTimeout: NodeJS.Timeout | null = null;
  private retryAttempts = 0;
  private readonly maxRetries = 3;
  private readonly eventTarget = new EventTarget();
  private readonly LOAD_TIMEOUT_LIVE = 60_000;
  private readonly LOAD_TIMEOUT_NORMAL = 30_000;
  private readonly FADE_UPDATE_INTERVAL = 16;

  /** Initialize the audio element and event listeners */
  init(): void {
    if (this.isInitialized || !this.isClient()) {
      return;
    }

    this.isInitialized = true;

    if (this.isClient()) {
      this.audio = new Audio();
      this.setupEventListeners();
    }
  }

  private setupEventListeners(): void {
    if (!this.isClient()) {
      return;
    }

    const audio = this.ensureAudio();
    if (!audio) {
      return;
    }

    audio.addEventListener("error", () => {
      const errorDetails = {
        code: audio.error?.code,
        message: audio.error?.message,
        event_type: `error_code_${audio.error?.code || "unknown"}`,
        src: audio.src,
        readyState: audio.readyState,
        networkState: audio.networkState,
        raw_error_object: audio.error,
      };
      console.error("### Detailed Audio Element Error ###", errorDetails);

      if (this.retryAttempts < this.maxRetries) {
        this.retryAttempts += 1;

        setTimeout(() => {
          this.reloadAudio();
        }, 1000);
      }

      this.eventTarget.dispatchEvent(new CustomEvent("audioError"));
    });

    audio.addEventListener("playing", () => {
      this.retryAttempts = 0;
      this.eventTarget.dispatchEvent(new CustomEvent("bufferingEnd"));
      this.eventTarget.dispatchEvent(new CustomEvent("playbackStarted"));
    });

    audio.addEventListener("canplaythrough", () => {
      this.retryAttempts = 0;
      this.eventTarget.dispatchEvent(new CustomEvent("bufferingEnd"));
    });

    audio.addEventListener("waiting", () => {
      this.eventTarget.dispatchEvent(new CustomEvent("bufferingStart"));
    });

    audio.addEventListener("progress", () => {
      const buffered = audio.buffered;
      const currentTime = audio.currentTime;
      let bufferedEnd = 0;

      if (buffered.length === 0) {
        return;
      }

      for (let i = buffered.length - 1; i >= 0; i--) {
        if (buffered.start(i) <= currentTime) {
          bufferedEnd = buffered.end(i);
          break;
        }
      }

      if (bufferedEnd === 0) {
        bufferedEnd = buffered.end(0);
      }

      if (bufferedEnd > 0) {
        this.eventTarget.dispatchEvent(
          new CustomEvent("bufferUpdate", {
            detail: { bufferedTime: bufferedEnd },
          })
        );
      }
    });
  }

  cleanup(): void {
    if (this.audio) {
      this.audio.pause();
      this.audio.src = "";
      this.audio.load();
    }

    if (this.fadeTimeout) {
      clearTimeout(this.fadeTimeout);
      this.fadeTimeout = null;
    }

    this.playPromise = null;
  }

  getAudioElement(): HTMLAudioElement | null {
    if (!this.isClient()) {
      return null;
    }
    return this.audio;
  }

  private isClient(): boolean {
    return typeof window !== "undefined" && !!window.document;
  }

  private ensureAudio(): HTMLAudioElement {
    if (!this.isClient()) {
      throw new Error("Audio module not available on server side");
    }
    if (!this.audio) {
      throw new Error("Audio module not initialized");
    }
    return this.audio;
  }

  /**
   * Executes a function only if on client side
   */
  private ifClient<T>(fn: () => T): T | undefined {
    if (!this.isClient()) {
      return;
    }
    return fn();
  }

  async load(params: LoadParams): Promise<void> {
    const { url, startTime = 0, isLiveStream = false } = params;
    const result = this.ifClient(() =>
      this._load({ url, startTime, isLiveStream })
    );
    if (result) {
      await result;
    }
  }

  private async _load(params: {
    url: string;
    startTime: number;
    isLiveStream: boolean;
  }): Promise<void> {
    const { url, startTime, isLiveStream } = params;
    const audio = this.ensureAudio();
    if (!audio) {
      return;
    }

    try {
      this.retryAttempts = 0;
      if (audio.src === url) {
        if (audio.currentTime !== startTime && !isLiveStream) {
          audio.currentTime = startTime;
        }
        return;
      }

      audio.pause();
      audio.src = "";

      audio.src = url;
      audio.preload = "auto";

      const loadTimeout = isLiveStream
        ? this.LOAD_TIMEOUT_LIVE
        : this.LOAD_TIMEOUT_NORMAL;

      await new Promise<void>((resolve, reject) => {
        let timeoutId: NodeJS.Timeout | null = null;
        let isResolved = false;

        const cleanup = () => {
          if (timeoutId) {
            clearTimeout(timeoutId);
            timeoutId = null;
          }
          audio.removeEventListener("loadedmetadata", handleLoadSuccess);
          audio.removeEventListener("canplay", handleLoadSuccess);
          audio.removeEventListener("canplaythrough", handleLoadSuccess);
          audio.removeEventListener("error", handleErrorLoading);
        };

        const handleTimeout = () => {
          if (isResolved) {
            return;
          }
          isResolved = true;
          cleanup();

          reject(
            new Error(
              `Audio load timeout (${loadTimeout / 1000}s). ReadyState: ${audio.readyState}, NetworkState: ${audio.networkState}, URL: ${audio.src}`
            )
          );
        };

        const handleLoadSuccess = () => {
          if (isResolved) {
            return;
          }
          isResolved = true;
          cleanup();

          // Don't set currentTime for live streams
          if (startTime > 0 && !isLiveStream) {
            audio.currentTime = startTime;
          }
          resolve();
        };

        const handleErrorLoading = () => {
          if (isResolved) {
            return;
          }
          isResolved = true;
          cleanup();
          console.error("Error during initial audio load:", audio.error);
          reject(
            new Error(
              `Audio load failed: ${audio.error?.message || "Unknown error"}`
            )
          );
        };

        timeoutId = setTimeout(handleTimeout, loadTimeout);

        audio.addEventListener("loadedmetadata", handleLoadSuccess);
        audio.addEventListener("canplay", handleLoadSuccess);
        audio.addEventListener("canplaythrough", handleLoadSuccess);
        audio.addEventListener("error", handleErrorLoading);
        audio.load();
      });
    } catch (error) {
      console.error("Audio load process error:", error);
      throw error;
    }
  }

  async play(): Promise<void> {
    const result = this.ifClient(() => this._play());
    if (result) {
      await result;
    }
  }

  private async _play(): Promise<void> {
    if (!this.audio) {
      throw new Error("Audio module not initialized");
    }

    try {
      if (!this.audio.paused) {
        return;
      }
      this.playPromise = this.audio.play();
      await this.playPromise;
      this.playPromise = null;
    } catch (error) {
      this.playPromise = null;
      const errorDetails = {
        error,
        message: error instanceof Error ? error.message : "Unknown error",
        stack: error instanceof Error ? error.stack : undefined,
        url: this.audio.src,
        readyState: this.audio.readyState,
        networkState: this.audio.networkState,
      };
      console.error("Play error:", errorDetails);
      throw error;
    }
  }

  private reloadAudio(): void {
    if (!this.isClient()) {
      return;
    }

    const audio = this.ensureAudio();
    const currentTime = audio.currentTime;
    const wasPlaying = !audio.paused;
    const currentSrc = audio.src;

    audio.pause();
    audio.src = "";
    audio.load();
    audio.src = currentSrc;
    audio.preload = "auto";
    audio.load();

    const setTimeAndPlay = () => {
      if (audio.readyState >= audio.HAVE_METADATA) {
        audio.currentTime = currentTime;
        if (wasPlaying) {
          this.play().catch(console.error);
        }
        audio.removeEventListener("loadedmetadata", setTimeAndPlay);
      }
    };

    audio.addEventListener("loadedmetadata", setTimeAndPlay);
  }

  pause(): void {
    this.ifClient(() => {
      const audio = this.ensureAudio();
      audio.pause();
    });
  }

  setVolume(params: SetVolumeParams): void {
    const { volume, fadeTime = 0 } = params;
    this.ifClient(() => {
      const audio = this.ensureAudio();

      if (this.fadeTimeout) {
        clearTimeout(this.fadeTimeout);
        this.fadeTimeout = null;
      }

      if (fadeTime <= 0) {
        audio.volume = Math.max(0, Math.min(1, volume));
        if (volume > 0) {
          this.lastVolume = volume;
        }
        return;
      }

      this.fadeVolume({
        audio,
        targetVolume: volume,
        duration: fadeTime,
      });
    });
  }

  private fadeVolume(params: FadeVolumeParams): void {
    const { audio, targetVolume, duration } = params;
    if (!this.isClient()) {
      return;
    }

    const startVolume = audio.volume;
    const endVolume = Math.max(0, Math.min(1, targetVolume));
    const startTime = performance.now();

    const updateVolume = () => {
      const elapsed = performance.now() - startTime;
      const progress = Math.min(1, elapsed / duration);
      const currentVolume = startVolume + (endVolume - startVolume) * progress;

      audio.volume = currentVolume;

      if (progress < 1) {
        this.fadeTimeout = setTimeout(updateVolume, this.FADE_UPDATE_INTERVAL);
      } else {
        if (endVolume > 0) {
          this.lastVolume = endVolume;
        }
        this.fadeTimeout = null;
      }
    };

    updateVolume();
  }

  getVolume(): number {
    return (
      this.ifClient(() => {
        const audio = this.ensureAudio();
        return audio.volume;
      }) ?? 0
    );
  }

  setMuted(muted: boolean): void {
    this.ifClient(() => {
      const audio = this.ensureAudio();
      if (audio.muted === muted) {
        return;
      }

      if (muted) {
        if (audio.volume > 0) {
          this.lastVolume = audio.volume;
        }
        this.fadeVolume({ audio, targetVolume: 0, duration: 200 });
        audio.muted = true;
      } else {
        audio.muted = false;
        this.fadeVolume({
          audio,
          targetVolume: this.lastVolume,
          duration: 200,
        });
      }
    });
  }

  getDuration(): number {
    return (
      this.ifClient(() => {
        const audio = this.ensureAudio();
        return audio.duration;
      }) ?? 0
    );
  }

  getCurrentTime(): number {
    return (
      this.ifClient(() => {
        const audio = this.ensureAudio();
        return audio.currentTime;
      }) ?? 0
    );
  }

  setCurrentTime(time: number): void {
    this.ifClient(() => {
      const audio = this.ensureAudio();
      const duration = audio.duration;

      if (Number.isNaN(duration)) {
        return;
      }

      const isValidTime = time >= 0 && time <= duration;
      const validTime = isValidTime
        ? time
        : Math.max(0, Math.min(time, duration));

      if (audio.readyState >= audio.HAVE_METADATA) {
        audio.currentTime = validTime;
      }
    });
  }

  addEventListener(
    type: string,
    listener: EventListenerOrEventListenerObject | null,
    options?: boolean | AddEventListenerOptions
  ): void {
    this.eventTarget.addEventListener(type, listener, options);
  }

  removeEventListener(
    type: string,
    callback: EventListenerOrEventListenerObject | null,
    options?: EventListenerOptions | boolean
  ): void {
    this.eventTarget.removeEventListener(type, callback, options);
  }

  getSource(): string {
    return (
      this.ifClient(() => {
        const audio = this.ensureAudio();
        return audio.src;
      }) ?? ""
    );
  }

  isPaused(): boolean {
    return (
      this.ifClient(() => {
        const audio = this.ensureAudio();
        return audio.paused;
      }) ?? true
    );
  }

  getBufferedRanges(): TimeRanges | null {
    if (!(this.isClient() && this.audio)) {
      return null;
    }
    return this.audio.buffered;
  }

  setPlaybackRate(rate: number): void {
    this.ifClient(() => {
      const audio = this.ensureAudio();
      const url = audio.src.toLowerCase();

      // Check if current audio is a live stream using the same patterns as isLive()
      const isLiveStream = LIVE_STREAM_PATTERNS.some((pattern) =>
        pattern.test(url)
      );

      // Don't allow playback rate changes for live streams
      if (isLiveStream) {
        return;
      }

      const clampedRate = Math.max(0.25, Math.min(2, rate));
      audio.playbackRate = clampedRate;
    });
  }

  getPlaybackRate(): number {
    return (
      this.ifClient(() => {
        const audio = this.ensureAudio();
        return audio.playbackRate;
      }) ?? 1
    );
  }
}

export const $audio = new AudioLib();

const MINUTE_IN_SECONDS = 60;

export function isLive(track: Track): boolean {
  if (!track.url) {
    return false;
  }

  const url = track.url.toLowerCase();

  for (const pattern of LIVE_STREAM_PATTERNS) {
    if (pattern.test(url)) {
      return true;
    }
  }

  if (track.live === true) {
    return true;
  }

  return false;
}

export function formatDuration(seconds: number): string {
  if (!Number.isFinite(seconds) || seconds < 0) {
    return "0:00";
  }
  const minutes = Math.floor(seconds / MINUTE_IN_SECONDS);
  const remainingSeconds = Math.floor(seconds % MINUTE_IN_SECONDS);
  return `${minutes}:${remainingSeconds < 10 ? "0" : ""}${remainingSeconds}`;
}

import { create } from "zustand";
import { persist, subscribeWithSelector } from "zustand/middleware";
import { $audio, isLive, type Track } from "@/registry/default/lib/audio";

type RepeatMode = "none" | "one" | "all";
type InsertMode = "first" | "last" | "after";

type AudioStore = {
  // State
  currentTrack: Track | null;
  queue: Track[];
  isPlaying: boolean;
  isLoading: boolean;
  isBuffering: boolean;
  volume: number;
  isMuted: boolean;
  playbackRate: number;
  repeatMode: RepeatMode;
  shuffleEnabled: boolean;
  currentTime: number;
  duration: number;
  progress: number;
  bufferedTime: number;
  insertMode: InsertMode;
  isError: boolean;
  errorMessage: string | null;
  currentQueueIndex: number;

  // Playback Actions
  play: () => void;
  pause: () => void;
  togglePlay: () => void;
  next: () => void;
  previous: () => void;
  seek: (time: number) => void;
  setQueueAndPlay: (tracks: Track[], startIndex: number) => Promise<void>;
  handleTrackEnd: () => void;

  // Queue Actions
  addToQueue: (track: Track, mode?: InsertMode) => void;
  removeFromQueue: (trackId: string) => void;
  clearQueue: () => void;
  moveInQueue: (fromIndex: number, toIndex: number) => void;
  setQueue: (tracks: Track[], startIndex?: number) => void;
  getCurrentQueueIndex: () => number;
  addTracksToEndOfQueue: (tracksToAdd: Track[]) => void;

  // Control Actions
  setVolume: (params: { volume: number }) => void;
  toggleMute: () => void;
  setPlaybackRate: (rate: number) => void;
  changeRepeatMode: () => void;
  setInsertMode: (mode: InsertMode) => void;
  shuffle: () => void;
  unshuffle: () => void;
  setRepeatMode: (mode: RepeatMode) => void;

  // State Actions
  setCurrentTrack: (track: Track | null) => Promise<void>;
  setError: (message: string | null) => void;
};

function canUseDOM() {
  return !!(
    typeof window !== "undefined" &&
    window.document &&
    window.document.createElement
  );
}

type QueueNavigationParams = {
  queue: Track[];
  currentQueueIndex: number;
  shuffleEnabled: boolean;
  repeatMode: RepeatMode;
};

type GetRandomShuffleIndexParams = {
  queueLength: number;
  currentIndex: number;
};

/**
 * Calculates a random index for shuffle mode
 */
const getRandomShuffleIndex = (params: GetRandomShuffleIndexParams): number => {
  const { queueLength, currentIndex } = params;
  if (queueLength === 1) {
    return 0;
  }
  let randomIndex: number;
  do {
    randomIndex = Math.floor(Math.random() * queueLength);
  } while (randomIndex === currentIndex);
  return randomIndex;
};

type CalculateQueueIndexParams = QueueNavigationParams & {
  direction: 1 | -1;
};

/**
 * Calculates the next or previous index in the queue based on direction
 */
const calculateQueueIndex = (params: CalculateQueueIndexParams): number => {
  const { queue, currentQueueIndex, shuffleEnabled, repeatMode, direction } =
    params;

  if (queue.length === 0) {
    return -1;
  }

  if (shuffleEnabled) {
    const singleTrackIndex = repeatMode === "none" ? -1 : 0;
    return queue.length === 1
      ? singleTrackIndex
      : getRandomShuffleIndex({
          queueLength: queue.length,
          currentIndex: currentQueueIndex,
        });
  }

  const newIndex = currentQueueIndex + direction;
  const isAtEnd = newIndex >= queue.length;
  const isAtStart = newIndex < 0;

  if (isAtEnd) {
    return repeatMode === "all" ? 0 : -1;
  }

  if (isAtStart) {
    return repeatMode === "all" ? queue.length - 1 : -1;
  }

  return newIndex;
};

/**
 * Calculates the index of the next track
 */
const calculateNextIndex = (params: QueueNavigationParams): number =>
  calculateQueueIndex({
    ...params,
    direction: 1,
  });

/**
 * Calculates the index of the previous track
 */
const calculatePreviousIndex = (params: QueueNavigationParams): number =>
  calculateQueueIndex({
    ...params,
    direction: -1,
  });

/**
 * Default state after successful track loading
 */
const getSuccessState = (params: { isPlaying?: boolean } = {}) => ({
  isLoading: false,
  isError: false,
  errorMessage: null,
  isBuffering: false,
  isPlaying: params.isPlaying ?? false,
});

/**
 * Default state after loading error
 */
const getErrorState = (params: { errorMessage: string }) => ({
  isLoading: false,
  isPlaying: false,
  isError: true,
  errorMessage: params.errorMessage,
  isBuffering: false,
});

type LoadAndPlayTrackParams = {
  track: Track;
  queueIndex: number;
  set: (partial: Partial<AudioStore>) => void;
  get: () => AudioStore;
  errorMessage: string;
};

/**
 * Loads and plays a track with error handling
 */
const loadAndPlayTrack = async (
  params: LoadAndPlayTrackParams
): Promise<void> => {
  const { track, queueIndex, set, get, errorMessage } = params;
  const isLiveStream = isLive(track);

  set({
    currentTrack: track,
    currentQueueIndex: queueIndex,
    isLoading: true,
    isBuffering: true,
  });

  try {
    await $audio.load({
      url: track.url,
      startTime: 0,
      isLiveStream,
    });

    // Reset playback rate to 1.0 for live streams
    if (isLiveStream) {
      const currentState = get();
      if (currentState.playbackRate !== 1) {
        $audio.setPlaybackRate(1);
        set({ playbackRate: 1 });
      }
    }

    await $audio.play();
    set(getSuccessState({ isPlaying: true }));
  } catch (error) {
    console.error(errorMessage, error);
    set(getErrorState({ errorMessage }));
    throw error;
  }
};

const useAudioStore = create<AudioStore>()(
  persist(
    subscribeWithSelector((set, get) => ({
      // Initial State
      currentTrack: null,
      queue: [],
      isPlaying: false,
      isLoading: false,
      isBuffering: false,
      volume: 1,
      isMuted: false,
      playbackRate: 1,
      repeatMode: "none",
      shuffleEnabled: false,
      currentTime: 0,
      duration: 0,
      progress: 0,
      bufferedTime: 0,
      insertMode: "last",
      isError: false,
      errorMessage: null,
      currentQueueIndex: -1,

      // Playback Actions
      async play() {
        if (get().isLoading) {
          return;
        }
        await $audio.play();
      },

      pause() {
        $audio.pause();
      },

      togglePlay() {
        if (get().isLoading) {
          return;
        }
        if ($audio.isPaused()) {
          get().play();
        } else {
          get().pause();
        }
      },

      async next() {
        const state = get();
        const nextIndex = calculateNextIndex({
          queue: state.queue,
          currentQueueIndex: state.currentQueueIndex,
          shuffleEnabled: state.shuffleEnabled,
          repeatMode: state.repeatMode,
        });

        const nextTrack = state.queue[nextIndex];
        if (nextIndex === -1 || !nextTrack) {
          $audio.pause();
          set({ isLoading: false, isPlaying: false, isBuffering: false });
          return;
        }

        await loadAndPlayTrack({
          track: nextTrack,
          queueIndex: nextIndex,
          set,
          get,
          errorMessage: "Error loading/playing next track",
        });
      },

      async previous() {
        const state = get();
        const currentTime = $audio.getCurrentTime();
        const RESTART_THRESHOLD = 3;

        // If track has more than 3 seconds and shuffle is not enabled, restart the track
        if (currentTime > RESTART_THRESHOLD && !state.shuffleEnabled) {
          set({ isLoading: true });
          try {
            $audio.setCurrentTime(0);
            set({ currentTime: 0, progress: 0, isLoading: false });
            return;
          } catch (error) {
            console.error("Error restarting current track:", error);
            set({ isLoading: false });
            return;
          }
        }

        const prevIndex = calculatePreviousIndex({
          queue: state.queue,
          currentQueueIndex: state.currentQueueIndex,
          shuffleEnabled: state.shuffleEnabled,
          repeatMode: state.repeatMode,
        });

        const prevTrack = state.queue[prevIndex];
        if (prevIndex === -1 || !prevTrack) {
          if (prevIndex !== -1) {
            console.error(
              "Inconsistency: previous index is valid but track not found"
            );
          }
          $audio.pause();
          set({ isLoading: false, isPlaying: false, isBuffering: false });
          return;
        }

        await loadAndPlayTrack({
          track: prevTrack,
          queueIndex: prevIndex,
          set,
          get,
          errorMessage: "Error loading/playing previous track",
        });
      },

      seek(time: number) {
        $audio.setCurrentTime(time);
        set({ currentTime: time });
      },

      async setQueueAndPlay(songs: Track[], startIndex: number) {
        const targetTrack = songs[startIndex];
        if (!targetTrack) {
          console.error("[Playback] Invalid startIndex for setQueueAndPlay");
          get().clearQueue();
          $audio.pause();
          set({
            isPlaying: false,
            isLoading: false,
            currentTrack: null,
            currentQueueIndex: -1,
          });
          return;
        }

        get().setQueue(songs, startIndex);

        const errorMessage = `Error playing ${targetTrack.title || "track"}`;
        await loadAndPlayTrack({
          track: targetTrack,
          queueIndex: startIndex,
          set,
          get,
          errorMessage,
        });
      },

      handleTrackEnd() {
        get().next();
      },

      // Queue Actions
      setQueue(tracks: Track[], startIndex = 0) {
        const currentTrack = tracks[startIndex] ?? null;
        set({
          queue: tracks,
          currentQueueIndex: currentTrack ? startIndex : -1,
          currentTrack,
        });
      },

      getCurrentQueueIndex() {
        return get().currentQueueIndex;
      },

      addToQueue(track: Track, mode = "last") {
        const state = get();
        if (!state.currentTrack) {
          set({
            currentTrack: track,
            currentQueueIndex: 0,
            queue: [track],
          });
          return;
        }

        switch (mode) {
          case "first":
            set({
              queue: [track, ...state.queue],
              currentQueueIndex: state.currentQueueIndex + 1,
            });
            break;
          case "after":
            set({
              queue: [
                ...state.queue.slice(0, state.currentQueueIndex + 1),
                track,
                ...state.queue.slice(state.currentQueueIndex + 1),
              ],
            });
            break;
          default:
            set({ queue: [...state.queue, track] });
        }
      },

      removeFromQueue(trackId) {
        const state = get();
        const index = state.queue.findIndex((s) => s.id === trackId);
        if (index === -1) {
          return;
        }

        const newQueue = state.queue.filter((s) => s.id !== trackId);
        set({
          queue: newQueue,
          currentQueueIndex:
            index < state.currentQueueIndex
              ? state.currentQueueIndex - 1
              : state.currentQueueIndex,
        });
      },

      clearQueue() {
        set({ queue: [] });
      },

      moveInQueue(fromIndex, toIndex) {
        const newQueue = [...get().queue];
        const [movedItem] = newQueue.splice(fromIndex, 1);
        if (!movedItem) {
          return;
        }

        newQueue.splice(toIndex, 0, movedItem);
        set({ queue: newQueue });
      },

      addTracksToEndOfQueue(tracksToAdd: Track[]) {
        if (!tracksToAdd || tracksToAdd.length === 0) {
          return;
        }

        const state = get();
        const currentQueueIds = new Set(state.queue.map((s) => s.id));
        const newTracks = tracksToAdd.filter(
          (track) => !currentQueueIds.has(track.id)
        );

        if (newTracks.length > 0) {
          set({ queue: [...state.queue, ...newTracks] });
        }
      },

      // Control Actions
      setVolume(params: { volume: number }) {
        const { volume } = params;
        $audio.setVolume({ volume });
        set({ volume, isMuted: volume === 0 });
      },

      toggleMute() {
        const newMuted = !get().isMuted;
        $audio.setMuted(newMuted);
        set({ isMuted: newMuted });
      },

      setPlaybackRate(rate: number) {
        const state = get();
        // Don't allow playback rate changes for live streams
        if (state.currentTrack && isLive(state.currentTrack)) {
          return;
        }
        const clampedRate = Math.max(0.25, Math.min(2, rate));
        $audio.setPlaybackRate(clampedRate);
        set({ playbackRate: clampedRate });
      },

      changeRepeatMode() {
        const modes: RepeatMode[] = ["none", "one", "all"];
        const currentIndex = modes.indexOf(get().repeatMode);
        const newMode = modes[(currentIndex + 1) % modes.length];
        set({ repeatMode: newMode });
      },

      setRepeatMode(mode) {
        set({ repeatMode: mode });
      },

      setInsertMode(mode) {
        set({ insertMode: mode });
      },

      shuffle() {
        const state = get();
        if (
          !state.queue.length ||
          state.queue.length < 2 ||
          !state.currentTrack
        ) {
          return;
        }

        const remainingQueue = state.queue.filter(
          (_, index) => index !== state.currentQueueIndex
        );
        const shuffledRemaining = remainingQueue.sort(
          () => Math.random() - 0.5
        );
        const newQueue = [state.currentTrack, ...shuffledRemaining];

        set({
          queue: newQueue,
          currentQueueIndex: 0,
          shuffleEnabled: true,
        });
      },

      unshuffle() {
        set({ shuffleEnabled: false });
      },

      // State Actions
      async setCurrentTrack(track: Track | null) {
        const state = get();

        if (!track) {
          $audio.cleanup();
          set({
            currentTrack: null,
            currentQueueIndex: -1,
            isPlaying: false,
            currentTime: 0,
            duration: 0,
            queue: [],
            isLoading: false,
            isError: false,
            errorMessage: null,
          });
          return;
        }

        // Avoid reloading the same track
        if (state.currentTrack?.id === track.id) {
          return;
        }

        const errorMessage = `Error: ${track.title || "Unknown track"}`;

        // Update queue with a single track
        set({
          currentTrack: track,
          queue: [track],
          currentQueueIndex: 0,
          isLoading: true,
          isPlaying: false,
          currentTime: 0,
          duration: 0,
          isError: false,
          errorMessage: null,
        });

        await loadAndPlayTrack({
          track,
          queueIndex: 0,
          set,
          get,
          errorMessage,
        });
      },
      setError: (message) => {
        set({
          isError: !!message,
          errorMessage: message,
          isLoading: false,
          isPlaying: false,
        });
      },
    })),
    {
      name: "audio:ui:store",
      partialize: (state) => ({
        currentTrack: state.currentTrack,
        queue: state.queue,
        volume: state.volume,
        isMuted: state.isMuted,
        playbackRate: state.playbackRate,
        repeatMode: state.repeatMode,
        shuffleEnabled: state.shuffleEnabled,
        currentTime: state.currentTime,
        insertMode: state.insertMode,
        currentQueueIndex: state.currentQueueIndex,
      }),
    }
  )
);

export {
  calculateNextIndex,
  calculatePreviousIndex,
  canUseDOM,
  useAudioStore,
  type AudioStore,
  type RepeatMode,
  type InsertMode,
