#!/usr/bin/env bun

import { exec } from "node:child_process";
import { promises as fs } from "node:fs";
import path from "node:path";
import { rimraf } from "rimraf";
import type { RegistryItem } from "shadcn/schema";
import { registry } from "../src/registry";

async function buildRegistryIndex() {
  let index = `
/** biome-ignore-all lint/suspicious/noExplicitAny: we use any to avoid type errors */
// @ts-nocheck
// This file is autogenerated by scripts/build-registry.ts
// Do not edit this file directly.
import React from "react" 

export const Index: Record<string, any> = {`;
  for (const item of registry.items) {
    const resolveFiles =
      item.files?.map(
        (file: (typeof item.files)[number]) =>
          `src/registry/default/${file.path}`
      ) ?? [];
    if (!resolveFiles) {
      continue;
    }

    const componentPath = item.files?.[0]?.path
      ? `@/registry/default/${item.files[0].path}`
      : "";

    index += `
  "${item.name}": {
    name: "${item.name}",
    description: "${item.description ?? ""}",
    type: "${item.type}",
    registryDependencies: ${JSON.stringify(item.registryDependencies)},
    files: [${item.files?.map((file: (typeof item.files)[number]) => {
      const filePath = `src/registry/default/${typeof file === "string" ? file : file.path}`;
      const resolvedFilePath = path.resolve(filePath);
      return typeof file === "string"
        ? `"${resolvedFilePath}"`
        : `{
      path: "${filePath}",
      type: "${file.type}",
      target: "${file.target ?? ""}"
    }`;
    })}],
    component: ${
      componentPath
        ? `React.lazy(async () => {
      const mod = await import("${componentPath}")
      const exportName = Object.keys(mod).find(key => typeof mod[key] === 'function' || typeof mod[key] === 'object') || item.name
      return { default: mod.default || mod[exportName] }
    })`
        : "null"
    },
    categories: ${JSON.stringify(item.categories)},
    meta: ${JSON.stringify(item.meta)},
  },`;
  }

  index += `
  }`;

  console.log(`#ï¸âƒ£  ${Object.keys(registry.items).length} items found`);

  rimraf.sync(path.join(process.cwd(), "src/registry/__index__.tsx"));
  await fs.writeFile(
    path.join(process.cwd(), "src/registry/__index__.tsx"),
    index
  );
}

async function buildRegistryJsonFile() {
  const fixedRegistry = {
    ...registry,
    items: registry.items.map((item: RegistryItem) => {
      const files = item.files?.map((file: (typeof item.files)[number]) => ({
        ...file,
        path: `src/registry/default/${file.path}`,
      }));

      return {
        ...item,
        files,
      };
    }),
  };

  rimraf.sync(path.join(process.cwd(), "registry.json"));
  await fs.writeFile(
    path.join(process.cwd(), "registry.json"),
    JSON.stringify(fixedRegistry, null, 2)
  );

  await fs.cp(
    path.join(process.cwd(), "registry.json"),
    path.join(process.cwd(), "public/r/registry.json"),
    { recursive: true }
  );
}

async function buildRegistry() {
  return await new Promise((resolve, reject) => {
    const process = exec("bunx shadcn build registry.json --output public/r");

    process.on("exit", (code) => {
      if (code === 0) {
        resolve(undefined);
      } else {
        reject(new Error(`Process exited with code ${code}`));
      }
    });
  });
}

try {
  console.log("ğŸ—‚ï¸ Building src/registry/__index__.tsx...");
  await buildRegistryIndex();

  console.log("ğŸ’… Building registry.json...");
  await buildRegistryJsonFile();

  console.log("ğŸ—ï¸ Building registry...");
  await buildRegistry();

  console.log("âœ… Registry build completed successfully!");
} catch (error) {
  console.error(error);
  process.exit(1);
}
